task autonomous()
{ ////epic code//////////////
	////4ft in 2.5 sec//
	motor(Leftside)=-127;
	motor(Rightside)=-127;
	wait(0.3);
	motor(Leftside)=127;
	motor(Rightside)=127;
	wait(0.2);
	motor(Leftside)=0;
	motor(Rightside)=0;
	motor(Leftlift1)=-127;
	motor(Rightlift1)=127;
	wait(2);
	motor(Leftlift1)=0;
	motor(Rightlift1)=0;




	////fin

}

