int gotomovement = 6969;
task movement()
{

	motor[Rightside] = vexRT[Ch2];
	motor[Leftside] = vexRT[Ch3];


	if(vexRT[Btn6U]==1)
	{
		motor[MiddleMotor]=64;
	}

	else if(vexRT[Btn6D]==1)
	{
		motor[MiddleMotor]=127;
	}

	else if(vexRT[Btn5U]==1)
	{
		motor[MiddleMotor]=-64;
	}

	else if(vexRT[Btn5D]==1)
	{
		motor[MiddleMotor]=-127;
	}

	else
	{
		stopMotor(MiddleMotor);

	}


}


